import React, { Component } from 'react'

export class AccountComponent extends Component {

    state = {
        deleteHover: false,
    }

    getAccountName(data){
        if(data === 'cash_account'){
            return 'Cash Account'
        }else if(data === 'bank_account'){
            return 'Bank Account'
        }else if(data === 'wallet_account'){
            return 'Wallet'
        }else{
            return 'NA'
        }
    }
    render() {
        return (
            <div className="card">                               
                <div className="card-body">                                                     
                    <div className="row">
                        <div className="col">
                            <div className="media">
                                <div className="media-body ml-3 align-self-center">
                                    <h5 className="mt-0 mb-1">{this.props.account?this.props.account.account_name:""}</h5> 
                                    <p className="mb-0 text-muted">{this.getAccountName(this.props.account?this.props.account.account_type:"")}</p>
                                </div>
                            </div>
                        </div>
                        <div className="col-auto align-self-center">
                            <ul className="list-inline mb-0">
                                <li className="list-inline-item">
                                    <a href="#" className="mr-1 contact-icon"><i className="mdi mdi-bank"></i></a>
                                </li>
                                <li className="list-inline-item">
                                    <a href="#" className="mr-1 contact-icon"><i className="mdi mdi-circle-edit-outline"></i></a>
                                </li>
                                <li className="list-inline-item" onMouseEnter={()=>this.setState({deleteHover: true})} onMouseLeave={()=>this.setState({deleteHover: false})}>
                                    <a href="#" className="mr-1 contact-icon" style={{ color: 'red' }}><i className="mdi mdi-trash-can-outline"></i></a>
                                </li>
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
        )
    }

}

export default  AccountComponent;
