import React, { Component } from 'react'
import { Button } from 'antd'
import { logout, resetPassword, deactivateUser} from '../../../../Store/Action/auth';
import { connect } from 'react-redux';

export class Security extends Component {


    constructor(props){
        super(props)
        this.deactivateAccount = this.deactivateAccount.bind(this)
    }

    //  Handle Logout
    handleLogout(){
        console.log('logging out')
        this.props.logout()
    }
    handlePasswordReset(){
        console.log(this.props.auth.user.email)
        // this.props.resetPassword(this.props.auth.user.email)
    }

    deactivateAccount(){
        console.log('working to deactivate')
        this.props.deactivateUser()
    }

    render() {
        return (
          <div>
            <h3>Security Settings</h3>
            These settings are helps you keep your account secure.
            <div className="row" style={{ marginTop: '60px' }}>
                <div className="col">
                    <h5>Change Password</h5>
                    <p style={{ color: '#8a8a8a' }}>Set a unique password to protect your account.</p>
                </div>
                <div className="col" style={{ textAlign: 'center' }}>
                    <Button type="primary" shape="round" onClick={()=>this.handlePasswordReset()}>Change Password</Button>
                </div>
            </div>
            <div className="row" style={{ marginTop: '20px' }}>
                <div className="col">
                    <h5>Logout</h5>
                    <p style={{ color: '#8a8a8a' }}>This does not delete your account, but just end the session</p>
                </div>
                <div className="col" style={{ textAlign: 'center' }}>
                    <Button type="danger" shape="round" onClick={()=>this.handleLogout()}>Logout</Button>
                </div>
            </div>
            <div className="row" style={{ marginTop: '20px' }}>
                <div className="col">
                    <h5>Deactivate Account</h5>
                    <p style={{ color: '#8a8a8a' }}>Data about this account will not be deleted from our server since your are deactivating. </p>
                </div>
                <div className="col" style={{ textAlign: 'center' }}>
                    <Button type="danger" shape="round" onClick={this.deactivateAccount}>Deactivate Account</Button>
                </div>
            </div>
          </div>
        )
    }

}

const mapStateToProps = (state) => ({
    isAuthenticated: state.auth.isAuthenticated,
    isLoading: state.auth.isLoading,
    auth: state.auth.user
  });


export default connect(mapStateToProps,{logout,resetPassword,deactivateUser})(Security);
