import {
    TRANSCATION_LOADED,
    TRANSCATION_LOADING,
    TRANSCATION_CREATED,
    TRANSCATION_DELETED,
    TRANSCATION_UPDATED
  } from '../Action/types';

  const initialState = {
    token: localStorage.getItem('token'),
    isLoading: false,
    transcation: null,
  };

  export default function (state = initialState ,action) {
    switch (action.type) {

      case TRANSCATION_LOADED:
        return {
            isLoading: false,
            transcation: action.payload.transcations,
        }; 
      case TRANSCATION_CREATED:
        return{
            isLoading: false,
            transcation: state.transcation.concat(action.payload.transcations)
        }
      case TRANSCATION_DELETED:
          return{
              isLoading: false,
              transcation: state.transcation.filter(data => data._id !== action.payload.transcations._id)
          }
      case TRANSCATION_UPDATED:
          return {
                isLoading: false,
                account: state.account.map((data,index)=> data._id === action.payload.accounts._id ? action.payload.accounts : data)
            }
    //   case ACCOUNT_ERROR: {
    //     return {
    //         ...state,
    //         error: action.payload,
    //         isLoading: false
    //       }
    //   }
      default:
        return state;
    }
  }
