import React, { Component } from 'react'
import { Row, Col, Button, message } from 'antd';
import { connect } from 'react-redux';
import { loadTranscation } from '../../../Store/Action/transcation'
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import  moment  from 'moment';

// Success Messge Box
const success = () => {
    message.success('This is a success message');
};

// Error Message Box
const error = (data) => {
    message.error(data);
};

export class Transcation extends Component {

    state = {
        transcations: []
    }

    componentDidMount(){
        this.props.loadTranscation()
        this.setState({
            transcations: this.props.transcation
        })
    }

    //  Fired when the component recieves new props
    componentWillReceiveProps(nextProps){
        if(nextProps.error !== undefined){
            if(nextProps.error.message === 'Network Error'){
                error(nextProps.error.message)
                this.setState({ transcations: nextProps.transcation })
            }else{
                this.setState({ transcations: nextProps.transcation })
                error(nextProps.error.response.data.error)
            }
        }
        this.setState({ transcations: nextProps.transcation })
    }

    render() {
        console.log(this.state.transcations)
        return (
          <div className="container-fluid">
            <Row>
                <Col span={12}>
                    {/* BreadCrumps */}
                    <div className="row">
                        <div className="col-lg-6">
                            <div className="page-title-box">
                                <div className="row">
                                    <div className="col">
                                        <h4 className="page-title">Transcations</h4>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </Col>
                <Col span={12}>
                    <Button type="primary" style={{ float: 'right', marginRight: '100px' }}>Add a Transcation</Button>
                </Col>
            </Row>
            <div style={{ padding: '50px' }}>
                <TableContainer>
                    <Table  aria-label="simple table">
                        <TableHead style={{ backgroundColor: '#f4f8ff' }}>
                            <TableRow>
                                <TableCell>Date</TableCell>
                                <TableCell align="center">Debit</TableCell>
                                <TableCell align="center">Amount</TableCell>
                                <TableCell align="center">Description</TableCell>
                                <TableCell align="center">Category</TableCell>
                                <TableCell align="center">Transcation Type</TableCell>
                                <TableCell align="center">From Account</TableCell>
                                <TableCell align="center">Category</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                        {this.state.transcations?this.state.transcations.map((row) => (
                            <TableRow key={row._id}>
                            <TableCell component="th" scope="row">{moment(row.createdAt).format('LLL')}</TableCell>
                            <TableCell align="center">{row.debit?"Debit":"Credit"}</TableCell>
                            <TableCell align="center">{row.amount}</TableCell>
                            <TableCell align="center">{row.description}</TableCell>
                            <TableCell align="center">{row.category}</TableCell>
                            <TableCell align="center">{row.transcation_type}</TableCell>
                            <TableCell align="center">{row.account.account_name}</TableCell>
                            <TableCell align="center">{row.category}</TableCell>
                            </TableRow>
                        )):""}
                        </TableBody>
                    </Table>
                </TableContainer>
            </div>
                <Modal
                    title="Add a New Account"
                    centered
                    visible={this.state.visible}
                    okText="Add Account"
                    onOk={() => this.handleAddAccount()}
                    onCancel={() => this.setState({visible: false})}
                    width={500}
                >
                    <Input placeholder="Account Number" name="account_id" style={{ margin: '10px' }} value={this.state.account_id} onChange={this.onChange}></Input>
                    <Input placeholder="Account Name" name="account_name" style={{ margin: '10px' }} value={this.state.account_name} onChange={this.onChange}></Input>
                    <Select name="account_type" style={{ width: '100%',margin: '10px' }} value={this.state.account_type} placeholder="Account Type" onSelect={(val,eve)=>this.onDropDownChange(val)}>
                        <Select.Option name="account_type" value="cash_account">Cash Account</Select.Option>
                        <Select.Option name="account_type" value="bank_account">Bank Account</Select.Option>
                        <Select.Option  name="account_type" value="wallet_account">Wallet Account</Select.Option>
                    </Select>                    
                    <Input placeholder="Initial Balance" value={this.state.initialBalance} name="initialBalance" type="number" onChange={this.onChange} prefix={<i className="mdi mdi-cash" style={{ color: 'grey' }}></i>} style={{ margin: '10px' }}></Input>
                    <Input placeholder="Provider eg. Indian Bank, Paytm" value={this.state.account_provider} name="account_provider" onChange={this.onChange} style={{ margin: '10px' }}></Input>
                </Modal>
          </div>
        )
    }

}

const mapStateToProps = (state) => ({
    isLoading: state.auth.isLoading,
    transcation: state.transcation.transcation,
  });


export default connect(mapStateToProps,{loadTranscation})(Transcation);
