import React, { Component } from 'react'
import { Row, Col, Button } from 'antd';
import { connect } from 'react-redux';
import { loadTranscation } from '../../../Store/Action/transcation'
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import  moment  from 'moment';

export class Transcation extends Component {

    state = {
        transcations: []
    }

    componentDidMount(){
        this.props.loadTranscation()
        this.setState({
            transcations: this.props.transcation
        })
    }

    //  Fired when the component recieves new props
    componentWillReceiveProps(nextProps){
        if(nextProps.error !== undefined){
            if(nextProps.error.message === 'Network Error'){
                error(nextProps.error.message)
                this.setState({ account: nextProps.account })
            }else{
                this.setState({ account: nextProps.account })
                error(nextProps.error.response.data.error)
            }
        }
        this.setState({ account: nextProps.account })
    }

    render() {
        console.log(this.state.transcations)
        return (
          <div className="container-fluid">
            <Row>
                <Col span={12}>
                    {/* BreadCrumps */}
                    <div className="row">
                        <div className="col-lg-6">
                            <div className="page-title-box">
                                <div className="row">
                                    <div className="col">
                                        <h4 className="page-title">Transcations</h4>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </Col>
                <Col span={12}>
                    <Button type="primary" style={{ float: 'right', marginRight: '100px' }}>Add a Transcation</Button>
                </Col>
            </Row>
            <div style={{ padding: '50px' }}>
                <TableContainer>
                    <Table  aria-label="simple table">
                        <TableHead style={{ backgroundColor: '#f4f8ff' }}>
                            <TableRow>
                                <TableCell>Date</TableCell>
                                <TableCell align="right">Debit</TableCell>
                                <TableCell align="right">Amount</TableCell>
                                <TableCell align="right">Description</TableCell>
                                <TableCell align="right">Category</TableCell>
                                <TableCell align="right">Transcation Type</TableCell>
                                <TableCell align="right">From Account</TableCell>
                                <TableCell align="right">Category</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                        {this.state.transcations?this.state.transcations.map((row) => (
                            <TableRow key={row.name}>
                            <TableCell component="th" scope="row">
                                {moment(row.createdAt).format('LLL')}
                            </TableCell>
                            <TableCell align="right">{row.calories}</TableCell>
                            <TableCell align="right">{row.fat}</TableCell>
                            <TableCell align="right">{row.carbs}</TableCell>
                            <TableCell align="right">{row.protein}</TableCell>
                            </TableRow>
                        )):""}
                        </TableBody>
                    </Table>
                </TableContainer>
            </div>
          </div>
        )
    }

}

const mapStateToProps = (state) => ({
    isLoading: state.auth.isLoading,
    transcation: state.transcation.transcation,
  });


export default connect(mapStateToProps,{loadTranscation})(Transcation);
