import {
    ACCOUNTS_LOADED,
    ACCOUNT_CREATED
  } from '../Action/types';

  const initialState = {
    token: localStorage.getItem('token'),
    isLoading: false,
    account: null,
  };

  export default function (state = initialState ,action) {
    switch (action.type) {

      case ACCOUNTS_LOADED:
        return {
            isLoading: false,
            account: action.payload,
        }; 
      case ACCOUNT_CREATED:
        return{
            isLoading: false,
            account: [...state.account,...action.payload.account]
        }
      default:
        return state;
    }
  }
